custom_css:
:root {
--bg: #072D51;
--bgBlur: ;

--border: #185471;
--glow: #248ABB;

--links: #82DAFF;
--break: #248ABB;
--warning: #45C3FF;

--sidePanelBG: rgba(7, 45, 81, 0.5);
--sidePanelBlur: 2px;
}

/* ----------------------------- */

/* --- Presets Panel --- */

/* Menu Background */
#left-nav-panel {
background-color: var(--sidePanelBG) !important;
backdrop-filter: blur(var(--sidePanelBlur)) !important;
}

/* Menu Borders */
#left-nav-panel, #left-nav-panel * {
border-color: var(--border) !important;
}

/* Menu Line Breaks */
#left-nav-panel hr {
background-image: linear-gradient(90deg, var(--transparent), var(--break), var(--transparent)) !important;
}

/* Links */
#left-nav-panel a {
color: var(--links) !important;
}

/* Sampler Order Background */
.prompt_order .ui-sortable-handle:hover {
background-color: var(--border) !important;
}

/* ----------------------------- */

/* --- API Panel --- */

/* Menu Background */
#rm_api_block {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#rm_api_block, #rm_api_block * {
border-color: var(--border) !important;
}

/* Links */
#rm_api_block a {
color: var(--links) !important;
}

/* ----------------------------- */

/* --- Advanced Formatting --- */

/* Menu Background */
#advanced-formatting-button .drawer-content {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Border */
#advanced-formatting-button .drawer-content, #advanced-formatting-button .drawer-content * {
border-color: var(--border) !important;
}

/* ----------------------------- */

/* --- Lorebooks --- */

/* Menu Background */
#WorldInfo {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#WorldInfo, #WorldInfo * {
border-color: var(--border) !important;
}

/* Line Breaks */
#WorldInfo hr {
background-image: linear-gradient(90deg, var(--transparent), var(--break), var(--transparent)) !important;
}

/* ----------------------------- */

/* --- User Settings --- */

/* Menu Background */
#user-settings-block {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#user-settings-block, #user-settings-block * {
border-color: var(--border) !important;
}

/* ----------------------------- */

/* --- Background Menu --- */

/* Menu Background */
#logo_block .drawer-content {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#logo_block .drawer-content, #logo_block .drawer-content * {
border-color: var(--border) !important;
}

/* ----------------------------- */

/* --- Extensions --- */
/* Menu Background */
#rm_extensions_block {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#rm_extensions_block, #rm_extensions_block * {
border-color: var(--border) !important;
}

/* Menu Line Breaks */
#rm_extensions_block hr {
background-image: linear-gradient(90deg, var(--transparent), var(--break), var(--transparent)) !important;
}

/* Links */
#rm_extensions_block a {
color: var(--links) !important;
}

/* Icons */
#rm_extensions_block i {
color: var(--icons) !important;
}


/* ----------------------------- */

/* ---Persona Management --- */

/* Menu Background */
#persona_show_notifications, #persona-management-button .drawer-content, #persona-management-button .drawer-content {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#persona-management-button .drawer-content, #persona-management-block .flex1 * {
border-color: var(--border) !important;
}

/* Links */
#persona-management-button a {
color: var(--links) !important;
}

/* Avatar Border */
.avatar-container * {
border-color: var(--border) !important;
}

/* Avatar Highlight */
.avatar.selected {
outline-color: var(--icons) !important;
}

/* Show Notifications Checkbox */
#persona_show_notifications {
box-shadow: inset 0 0 3px 0 var(--border) !important;
}

/* ----------------------------- */

/* --- Character Management --- */

/* Menu Background */
#right-nav-panel {
background-color: var(--sidePanelBG) !important;
backdrop-filter: blur(var(--sidePanelBlur)) !important;
}

/* Menu Borders */
#right-nav-panel, #right-nav-panel * {
border-color: var(--border) !important;
}

/* Menu Line Breaks */
#right-nav-panel hr {
background-image: linear-gradient(90deg, var(--transparent), var(--break), var(--transparent)) !important;

}

/* Starred Characters */
#right-nav-panel .character_select.is_fav * {
outline-color: var(--icons) !important;
color: var(--icons) !important;
}

/* --- Advanced Definitions --- */

/* Menu Background */
#character_popup {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#character_popup, #character_popup * {
border-color: var(--border) !important;
}

/* Menu Line Breaks */
#character_popup hr {
background-image: linear-gradient(90deg, var(--transparent), var(--break), var(--transparent)) !important;
}

/* ----------------------------- */

/* --- Dialog Popups --- */

/* Menu Background */
#dialogue_popup {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#dialogue_popup, #dialogue_popup * {
border-color: var(--border) !important;
}

/* ----------------------------- */

/* --- Chat Histories --- */

/* Menu Background */
#select_chat_popup {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
#select_chat_popup, #select_chat_popup * {
border-color: var(--border) !important;
}

/* ----------------------------- */

/* --- Dropdowns --- */

/* Background */
.select2-dropdown {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}


/* Borders */
.select2-dropdown, .select2-dropdown * {
border-color: var(--border) !important;
}

/* Checkboxes */
.select2-results__option:before {
background-color: var(--bg) !important;
border-color: var(--border) !important;
}

/* ----------------------------- */

/* --- Options --- */

/* Menu Background */
.options-content {
background-color: var(--bg) !important;
backdrop-filter: blur(var(--bgBlur)) !important;
}

/* Menu Borders */
.options-content, .options-content * {
border-color: var(--border) !important;
}

/* ----------------------------- */

/* --- Other --- */

/* Links */
a {
color: var(--links) !important;
}
